import React, { useState } from "react";
import LoadingAnimation from "../LoadingAnimation/LoadingAnimation";
import JobCards from "../JobCards/JobCards";

const LandingPage = () => {
  const [loading, setLoading] = useState(false);
  const [jobs, setJobs] = useState([]);
  const [view, setView] = useState("home");

  const fetchJobs = (type) => {
    setLoading(true);
    setView("loading");
    setTimeout(() => {
      const mockJobs = [
        { title: "Frontend Developer", company: "ABC Inc.", location: "NYC" },
        { title: "Intern - Data Science", company: "XYZ Ltd.", location: "Remote" },
      ];
      setJobs(mockJobs);
      setLoading(false);
      setView("results");
    }, 3000);
  };

  return (
    <div className="landing-page">
      {view === "home" && (
        <div className="search-container">
          <h2>Search for Jobs and Internships</h2>
          <input type="text" placeholder="Search here..." className="search-bar" />
          <div className="button-group">
            <button onClick={() => fetchJobs("jobs")}>Scrape Jobs Only</button>
            <button onClick={() => fetchJobs("internships")}>Scrape Internships Only</button>
          </div>
        </div>
      )}
      {view === "loading" && <LoadingAnimation />}
      {view === "results" && <JobCards jobs={jobs} />}
    </div>
  );
};

export default LandingPage;
