import React from "react";
import Navbar from "./Components/Navbar/Navbar";
import LandingPage from "./Components/Landingpage/LandingPage";
import "./App.css";

const App = () => {
  const scrollToSection = (sectionId) => {
    const section = document.getElementById(sectionId);
    section.scrollIntoView({ behavior: "smooth" });
  };

  return (
    <div className="app">
      <Navbar onNavClick={scrollToSection} />

      {/* Home Section */}
      <div id="home" className="section">
        <h1>Welcome to Our Website</h1>
        <p>Find your dream job or internship here.</p>
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search for jobs or internships..."
            className="search-input"
          />
          <button className="search-btn">Search</button>
        </div>
        <div className="action-buttons">
          <button className="scrape-btn">Scrape Jobs Only</button>
          <button className="scrape-btn">Scrape Internships Only</button>
        </div>
      </div>

      {/* Jobs Section */}
      <div id="jobs" className="section">
        <h1>Jobs</h1>
        <p>Explore the latest job opportunities!</p>
      </div>

      {/* Internships Section */}
      <div id="internships" className="section">
        <h1>Internships</h1>
        <p>Discover exciting internships to kickstart your career.</p>
      </div>

      {/* About Us Section */}
      <div id="about" className="section">
        <h1>About Us</h1>
        <p>Learn more about our mission and values.</p>
      </div>
    </div>
  );
};

export default App;
